"use strict";

// Add babel plugin
exports.onCreateBabelConfig = ({
  actions
}, pluginOptions) => {
  const plugins = pluginOptions.jsxPlugins,
        optimizeForSpeed = pluginOptions.optimizeForSpeed,
        sourceMaps = pluginOptions.sourceMaps,
        vendorPrefixes = pluginOptions.vendorPrefixes;
  actions.setBabelPlugin({
    name: `styled-jsx/babel`,
    options: Object.assign({
      optimizeForSpeed,
      sourceMaps,
      vendorPrefixes
    }, plugins ? {
      plugins
    } : {})
  });
};